#ifndef __EXAMPLES_FFT_RESOURCE_H
#define __EXAMPLES_FFT_RESOURCE_H

/* These IDs are sharing with dspmath */

#define DSP_MQID   10

/* Determine which is RPC, they are magic numbers. */

#define DSP_RPC_MSG    30
#define DSP_RPC_RET    40
#define DSP_RPC_UNDEF  41
#define DSP_RPC_UNLOAD 90

/* DSP Math function hashes.
 * These hashes are generated by following commands (by hands!).
 *
 * echo <function name> | sha1sum - | cut -d -8
 *
 * e.g. $ echo arm_fill_f32 | sha1sum - | cut -b -8
 */

#define DSP_INIT_FFT_F32          0xe5dd7e4c
#define DSP_EXEC_FFT_F32          0x3bf3d7f3   
#define DSP_FLUSH_FFT_F32         0xf6b19571

#define DSP_INIT_FFT_Q15          0xe5dd7e40
#define DSP_EXEC_FFT_Q15          0x3bf3d7f0
#define DSP_FLUSH_FFT_Q15         0xf6b19570


#endif /* __EXAMPLES_FFT_RESOURCE_H */
